/*Заглушка модуля криптования паролей для запуска на win платформе*/


exports.genSaltSync = function (num) {
	return 9*num;
};

exports.hashSync = function (pass, salt) {
	return "$" + pass + salt;
};

exports.compare = function (cand, pass, cb) {
	var salt = this.genSaltSync(10);
	var cryptPass = this.hashSync (cand, salt);
	cb(null, (cryptPass==pass));
};